function xyz(event){
    // Don't respond if not requested
    if (event.oadrResponseRequired === 'never'){
        return;
    }

    let optType;

    let ed = event.eiEvent.eventDescriptor;

    switch(ed.eventStatus.toLowerCase()){
        case('completed'):
        case('canceled'):
            optType = 'optOut';
            break;
        default: 
            optType = 'optIn';
            break;
    }

    if(optType === 'optIn'){
        switch(ed.eiMarketContext.marketContext.toLowerCase()){
            case('demand_response'):
            case('http://marketcontext1'):
                break;
            default:
                optType = 'optOut';
                break;
        }
    }

    optType = (ed.eventStatus.toLowerCase() === 'completed' )? 'optOut' : 'optIn';


    var createdEvent = {
        responseCode: "200",
        responseDescription: 'OK',
        //requestID: '11111111',
        qualifiedEventID : {
            eventID: ed.eventID,
            modificationNumber: ed.modificationNumber
        },
        optType: optType
    };
    console.log(createdEvent)
    return createdEvent;

}

