function doPolling(){
    var newPollingInt = flow.get(flowvar);
    if (newPollingInt === 0){
        clearInterval(it);
        node.status( { fill: "red", shape: "dot", text: "Polling: stopped"});
        return;
    }
    if (newPollingInt != pollInt){
        clearInterval(it);
        hbInt = newPollingInt;
        it = setInterval(doPolling, pollInt * 1000);
        node.status( { fill: "green", shape: "dot", text: "Polling sec: " + pollInt});
    }
    node.send(pollMsg);
}

